const cartBtn=document.getElementById('cart-btn');const cartContent=document.getElementById('cart-content');let cartVisible=!1;cartBtn.addEventListener('click',()=>{cartVisible=!cartVisible;cartContent.style.display=cartVisible?'block':'none'});let cart=[];const addToCartButtons=document.querySelectorAll('.add-to-cart');addToCartButtons.forEach(button=>{button.addEventListener('click',(event)=>{const product=event.target.getAttribute('data-product');addToCart(product)})});function addToCart(product){cart.push(product);updateCartUI()}
function removeFromCart(index){cart.splice(index,1);updateCartUI()}
function updateCartUI(){const cartItems=document.getElementById('cart-items');const cartCount=document.getElementById('cart-count');cartItems.innerHTML='';cart.forEach((product,index)=>{const newItem=document.createElement('li');newItem.innerHTML=`
            ${product} 
            <button onclick="removeFromCart(${index})">Eliminar</button>
        `;cartItems.appendChild(newItem)});cartCount.textContent=cart.length}
document.getElementById('contactForm').addEventListener('submit',function(event){event.preventDefault();const name=document.getElementById('name').value;const email=document.getElementById('email').value;const message=document.getElementById('message').value;console.log('Form submitted:',{name,email,message});alert('Thank you for contacting us!');document.getElementById('contactForm').reset()})